import React ,{useState,useEffect} from "react"
import {Paper, Step,StepLabel,Typography,CircularProgress, Divider ,Button ,Stepper} from "@material-ui/core"
import useStyles from "./styles"
import AddressForm from "../AddressForm"
import PaymentForm from "../PaymentForm"
import {commerce } from "../../../lib/commerce"

const Checkout=({cart})=>{
    const classes=useStyles();
    const [activeStep,setActiveStep]=useState(0)
    const steps=[' Shipping Address ','Payment']
   
    const [checkoutToken,setcheckoutToken]=useState(null)
    const [shippingData, setshippingData]=useState({})

useEffect(()=>{

    const generateToken=async()=>{
        try {
            const token=await commerce.checkout.generateToken(cart.id,{type:'cart'})
            setcheckoutToken(token)
            console.log(token)

            
        } catch (error) {

            
        }

    }

    generateToken();
},[cart])
    const Confirmation=()=>{
        return (
            <div>
                confirm
            </div>
        )
    }

    const nextStep=()=>{
        setActiveStep((prevStep)=>prevStep+1)
    }

    const backStep=()=>{
        setActiveStep((prevStep)=>prevStep-1)
    }

    const next=(data)=>{
        setshippingData(data);
        nextStep()

    }

    const Form=()=>(
        activeStep===0?<AddressForm checkoutToken={checkoutToken}  next={next}/>:<PaymentForm checkoutToken={checkoutToken}/>
    )
    return(
        <>
        <div className={classes.toolbar}/>
        <main className={classes.layout}>
        <Paper>
            <Typography variant="h4"> Checkout</Typography>
            <Stepper activeStep={activeStep} className={classes.stepper}>
            {steps.map((step)=>(
                <Step key={step}>
                    <StepLabel>{step}</StepLabel>
                </Step>

            ))}

            </Stepper>
            {activeStep===steps.length ?<Confirmation/>:checkoutToken&& <Form/>}
        </Paper>

      </main>
        
        </>
    )

}
export default Checkout
