import { Button, Container } from '@material-ui/core';
import React from 'react';
import useStyles from "./styles"
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';

const Cart=({cart})=>{
    const classes=useStyles()
    
    const emptyCart=()=>{
        return (
            <div>
                <Typography variant="h2">
                YOur cart is empty
                </Typography>
            </div>
        )

    }
    const filledCart=()=>{
       
        return(
            <div>
        <Grid container spacing={3}>
       { cart.line_items.map((item )=>(
           <Grid item xs={12} sm={4} key={item.id}>
           <div>
               {item.name}
           </div>
           </Grid>
        ))}

        </Grid>
        <div  className={classes.cardDetails}>
            <Typography variant="h3" > Subtotal: {cart. subtotal.formatted_with_symbol}</Typography>
            <div>
                <Button className={classes.emptyButton} size="large" type="button" variant="contained" color="secondary">Empty Cart</Button>
                <Button className={classes.checkout } size="large" type="button" variant="contained" color="primary">Checkout</Button>
            </div>


        </div>
        
        </div>)}

if(!cart.line_items.length)
return "Loading ..."
return(
        <Container>
        <Typography className={classes.title} variant="h3">Your Cart</Typography>
        {!cart.line_items.length?<emptyCart/>:<filledCart/>}
    </Container>)
      
 
    

}
export default Cart
